bool is_prime(ll x) {
    for(ll i = 2; i * i <= x;++i)
        if(x % i == 0)
            return false;
    return true;
}

void duplic(vector<ll>& vec) {
    sort(all(vec));
    auto last = unique(vec.begin(), vec.end());
    vec.erase(last, vec.end());
}

