int main() {

    cin.tie(0);
    std::ios_base::sync_with_stdio(false);

    precalc();
    const ll diff = 1'000'000'000'0;
    ll start7 = 7'000'000'000'0;
    ll end7 = 7'999'999'999'9;

    ll start8 = 8'000'000'000'0;
    ll end8 = 8'999'999'999'9;

    vector<ll> squares7, squares8;
    squares7.reserve(20000);
    squares8.reserve(20000);

    for(ll i = (ll)sqrt(start7); i * i <= end7 + diff / 10; ++ i)
        squares7.push_back(i * i);


    int cnt=0;
    for(ll i = (ll)sqrt(start8); i * i <= end8 + diff / 10; ++ i)
        squares8.push_back(i * i);

    vector<int> isprime_7(isz(squares7)), isprime_8(isz(squares8));

    for(int i = 0;i<isz(squares7);++i) {
        isprime_7[i] = primes[squares7[i]+diff];
    }

    for(int i = 0;i<isz(squares8);++i) {
        isprime_8[i] = primes[squares8[i]-diff];
    }
    // precalc end

    int t; cin >> t;

    vector<ll> ans;
    ans.reserve(1000);

    while(t--) {
        ll x; cin >> x;

        int idx7 = lower_bound(all(squares7), start7 + x * 10000000) - squares7.begin();
        int idx8 = lower_bound(all(squares8), start8 + x * 10000000) - squares8.begin();

        int max7 = lower_bound(all(squares7), start7 + (x + 1) * 10000000) - squares7.begin();
        int max8 = lower_bound(all(squares8), start8 + (x + 1) * 10000000) - squares8.begin();
        if(x == 999) {
            max7 = lower_bound(all(squares7), start7 + diff) - squares7.begin();
            max8 = lower_bound(all(squares8), start8 + diff) - squares8.begin();
        }

        ans.clear();

        while(idx7 < max7) {
            if(isprime_7[idx7])
                ans.push_back(squares7[idx7] - start7);
            ++idx7;
        }

        while(idx8 < max8) {
            if(isprime_8[idx8])
                ans.push_back(squares8[idx8] - start8);
            ++idx8;
        }

        duplic(ans);

        cout<< isz(ans) << ' ';
        for(auto& it : ans)
            cout << setw(10) << setfill('0') << it << ' ';
        cout << '\n';
    }



    return 0;
}
